/**
 * This simple sample has no external dependencies or session management, and shows the most basic
 * example of how to create a Lambda function for handling Alexa Skill requests.
 *
 * Examples:
 * One-shot model:
 *  User: "Alexa, tell Hello World to say hello"
 *  Alexa: "Hello World!"
 */

/**
 * App ID for the skill
 */
var APP_ID = undefined; //replace with "amzn1.echo-sdk-ams.app.[your-unique-value-here]";

/**
 * The AlexaSkill prototype and helper functions
 */
var AlexaSkill = require('./AlexaSkill');
var WellnessScoreIntentHandler = require('./WellnessScoreIntentHandler');
var AdvisorContactIntentHandler = require('./AdvisorContactIntentHandler');

/**
 * PrincipalSkill is a child of AlexaSkill.
 * To read more about inheritance in JavaScript, see the link below.
 *
 * @see https://developer.mozilla.org/en-US/docs/Web/JavaScript/Introduction_to_Object-Oriented_JavaScript#Inheritance
 */
var PrincipalSkill = function () {
    AlexaSkill.call(this, APP_ID);
};

// Extend AlexaSkill
PrincipalSkill.prototype = Object.create(AlexaSkill.prototype);
PrincipalSkill.prototype.constructor = PrincipalSkill;

PrincipalSkill.prototype.eventHandlers.onSessionStarted = function (sessionStartedRequest, session) {
    console.log("PrincipalSkill onSessionStarted requestId: " + sessionStartedRequest.requestId
        + ", sessionId: " + session.sessionId);
    // any initialization logic goes here
};

PrincipalSkill.prototype.eventHandlers.onLaunch = function (launchRequest, session, response) {
    console.log("PrincipalSkill onLaunch requestId: " + launchRequest.requestId + ", sessionId: " + session.sessionId);
    var speechOutput = "Welcome to the Principal, how can I help you?";
    var repromptText = "Tell me how I can help you?";
    response.ask(speechOutput, repromptText);
};

PrincipalSkill.prototype.eventHandlers.onSessionEnded = function (sessionEndedRequest, session) {
    console.log("PrincipalSkill onSessionEnded requestId: " + sessionEndedRequest.requestId
        + ", sessionId: " + session.sessionId);
    // any cleanup logic goes here
};

PrincipalSkill.prototype.intentHandlers = {
    // register custom intent handlers
    "WellnessScoreIntent": function (intent, session, response) {
        session.attributes.activeIntent = "WellnessScoreIntent";
        this.callActiveIntentHandler(intent, session, response);
    },
    "AdvisorContactIntent": function (intent, session, response) {
        session.attributes.activeIntent = "AdvisorContactIntent";
        this.callActiveIntentHandler(intent, session, response);
    },
    "NumberAnswerIntent": function (intent, session, response) {  
        session.attributes[session.attributes.question] = intent.slots.Number.value;
        this.callActiveIntentHandler(intent, session, response);
    },
    "PersonAnswerIntent": function (intent, session, response) {  
        session.attributes[session.attributes.question] = intent.slots.Person.value;
        this.callActiveIntentHandler(intent, session, response);
    },
    "DateAnswerIntent": function (intent, session, response) {  
        session.attributes[session.attributes.question] = intent.slots.Date.value;
        this.callActiveIntentHandler(intent, session, response);
    },
    "TimeAnswerIntent": function (intent, session, response) {  
        session.attributes[session.attributes.question] = intent.slots.Time.value;
        this.callActiveIntentHandler(intent, session, response);
    },
    "AMAZON.YesIntent": function (intent, session, response) {  
        session.attributes[session.attributes.question] = "yes";
        this.callActiveIntentHandler(intent, session, response);
    },
    "AMAZON.NoIntent": function (intent, session, response) {  
        session.attributes[session.attributes.question] = "no";
        this.callActiveIntentHandler(intent, session, response);
    },
    "AMAZON.HelpIntent": function (intent, session, response) {
        response.ask("You can say hello to me!", "You can say hello to me!");
    }
};

PrincipalSkill.prototype.callActiveIntentHandler = function(intent, session, response) {
    
    switch(session.attributes.activeIntent) {
        case "WellnessScoreIntent":
                WellnessScoreIntentHandler.respond(intent, session, response);
                break;
        case "AdvisorContactIntent":
                AdvisorContactIntentHandler.respond(intent, session, response);
                break;
    }
}

// Create the handler that responds to the Alexa Request.
exports.handler = function (event, context) {
    // Create an instance of the PrincipalSkill skill.
    var skill = new PrincipalSkill();
    skill.execute(event, context);
};

